#!/usr/bin/env bash
# DO NOT EDIT THIS FILE AS IT IS AUTOGENERATED!

# exit script when any command fails
set -e


# default values of variables set from params
_RUN_STEP_NODE=false
_RUN_STEP_YARN_SETUP=false
_RUN_STEP_VERSIONS=false
_RUN_STEP_YARN_INSTALL=false
_RUN_STEP_BROWSERSLIST=false
_RUN_STEP_ENV=false
_RUN_STEP_UPDATE_EMOJIS=false
_RUN_STEP_FORMAT=false
_RUN_STEP_ESLINT=false
_RUN_STEP_TSC=false
_RUN_STEP_USERSCRIPT=false
_RUN_STEP_BUILDSCRIPT=false
_RUN_STEP_PREBUILD=false
_RUN_STEP_WEBPACK=false
_RUN_STEP_DOCS=false
_RUN_STEP_GIT_DIFF=false
_RUN_STEP_ZIP=false
MODE="development"

while :; do
    case "${1-}" in
        --node) _RUN_STEP_NODE=true ;;
        --yarn_setup) _RUN_STEP_YARN_SETUP=true ;;
        --versions) _RUN_STEP_VERSIONS=true ;;
        --yarn_install) _RUN_STEP_YARN_INSTALL=true ;;
        --browserslist) _RUN_STEP_BROWSERSLIST=true ;;
        --env) _RUN_STEP_ENV=true ;;
        --update_emojis) _RUN_STEP_UPDATE_EMOJIS=true ;;
        --format) _RUN_STEP_FORMAT=true ;;
        --eslint) _RUN_STEP_ESLINT=true ;;
        --tsc) _RUN_STEP_TSC=true ;;
        --userscript) _RUN_STEP_USERSCRIPT=true ;;
        --buildscript) _RUN_STEP_BUILDSCRIPT=true ;;
        --prebuild) _RUN_STEP_PREBUILD=true ;;
        --webpack) _RUN_STEP_WEBPACK=true ;;
        --docs) _RUN_STEP_DOCS=true ;;
        --git_diff) _RUN_STEP_GIT_DIFF=true ;;
        --zip) _RUN_STEP_ZIP=true ;;
        --dependencies)
          _RUN_STEP_YARN_SETUP=true
          _RUN_STEP_VERSIONS=true
          _RUN_STEP_YARN_INSTALL=true
          _RUN_STEP_BROWSERSLIST=true ;;
        --quick)
          _RUN_STEP_ENV=true
          _RUN_STEP_FORMAT=true
          _RUN_STEP_ESLINT=true
          _RUN_STEP_TSC=true
          _RUN_STEP_WEBPACK=true ;;
        --full)
          _RUN_STEP_NODE=true
          _RUN_STEP_YARN_SETUP=true
          _RUN_STEP_VERSIONS=true
          _RUN_STEP_YARN_INSTALL=true
          _RUN_STEP_BROWSERSLIST=true
          _RUN_STEP_ENV=true
          _RUN_STEP_UPDATE_EMOJIS=true
          _RUN_STEP_FORMAT=true
          _RUN_STEP_ESLINT=true
          _RUN_STEP_TSC=true
          _RUN_STEP_USERSCRIPT=true
          _RUN_STEP_BUILDSCRIPT=true
          _RUN_STEP_PREBUILD=true
          _RUN_STEP_WEBPACK=true
          _RUN_STEP_DOCS=true
          _RUN_STEP_GIT_DIFF=true
          _RUN_STEP_ZIP=true ;;
        -p | --production) MODE="production" ;;
        -?*)
          echo "Unknown option: $1"
          exit 1 ;;
        *) break ;;
    esac
    shift
done

total_start_time=$(date +%s%N)

NODE_VERSION=$(grep '"node":' ./package.json | awk -F: '{ print $2 }' | sed 's/[",]//g' | sed 's/\^v//g' | tr -d '[:space:]')
YARN_VERSION=$(grep '"packageManager":' ./package.json | awk -F: '{ print $2 }' | sed 's/[",]//g' | sed 's/yarn@//g' | tr -d '[:space:]')
if [[ -n "$(git rev-parse --is-inside-work-tree 2>/dev/null)" ]]; then
    GIT_REPO=true
fi
if [[ $GIT_REPO = true ]]; then
    GIT_BRANCH=$(git branch --show-current)
    # Set ref to latest commit hash if HEAD is detached otherwise use branch name
    if [[ -z "$GIT_BRANCH" ]]; then
        REF=$(git rev-parse --short HEAD)
    else
        REF=$(git show-ref --heads --abbrev "$GIT_BRANCH" | grep -Po "(?<=[a-z0-9]{9} ).*$" --color=never)
    fi
else
    REF="dev"
fi

# [â¬†ï¸] Setup Node.js
if [[ $_RUN_STEP_NODE = true ]]; then
    start_time=$(date +%s%N)
    echo "### [â¬†ï¸] Setup Node.js ###"
    curl -o- https://raw.githubusercontent.com/nvm-sh/nvm/master/install.sh | bash
    if [[ -n "${NVM_DIR-}" ]]; then
        NVM_DIR="$NVM_DIR"
    elif [[ -n "${XDG_CONFIG_HOME-}" ]]; then
        NVM_DIR="${XDG_CONFIG_HOME}/nvm"
    else
        NVM_DIR="$HOME/.nvm"
    fi
    [ -s "$NVM_DIR/nvm.sh" ] && \. "$NVM_DIR/nvm.sh"
    nvm install "$NODE_VERSION"
    end_time=$(date +%s%N)
    echo "=== [â¬†ï¸] Setup Node.js: $(((end_time - start_time) / 1000000))ms ==="
fi

# [â¬†] setup yarn
if [[ $_RUN_STEP_YARN_SETUP = true ]]; then
    start_time=$(date +%s%N)
    echo "### [â¬†] setup yarn ###"
    corepack enable
    yarn set version "$YARN_VERSION"
    end_time=$(date +%s%N)
    echo "=== [â¬†] setup yarn: $(((end_time - start_time) / 1000000))ms ==="
fi

# [â„¹] print versions (node, yarn, git)
if [[ $_RUN_STEP_VERSIONS = true ]]; then
    start_time=$(date +%s%N)
    echo "### [â„¹] print versions (node, yarn, git) ###"
    echo "node: $(node -v) â€“ yarn: $(yarn -v) â€“ git: $(git --version)"
    end_time=$(date +%s%N)
    echo "=== [â„¹] print versions (node, yarn, git): $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸ±] yarn install
if [[ $_RUN_STEP_YARN_INSTALL = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸ±] yarn install ###"
    yarn install --immutable
    end_time=$(date +%s%N)
    echo "=== [ðŸ±] yarn install: $(((end_time - start_time) / 1000000))ms ==="
fi

# [â¬†] update browserslist
if [[ $_RUN_STEP_BROWSERSLIST = true ]]; then
    start_time=$(date +%s%N)
    echo "### [â¬†] update browserslist ###"
    npx -y browserslist@latest --update-db
    end_time=$(date +%s%N)
    echo "=== [â¬†] update browserslist: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸŒ³] set env variables
if [[ $_RUN_STEP_ENV = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸŒ³] set env variables ###"
    ref="$REF"
    BRANCH="dummy"
    
    if [[ $ref == "refs/heads/master" ]]; then
      BRANCH="stable"
    elif [[ $ref == "refs/heads/dev" ]]; then
      BRANCH="beta";
    elif [[ $ref == "refs/heads/"* ]]; then
      BRANCH="${ref/"refs/heads/"/}";
      BRANCH="${BRANCH/"/"/"-"}"
    elif [[ $ref == "refs/pull/"* ]]; then
      BRANCH="${ref/"refs/pull/"/"pr"}";
      BRANCH="${BRANCH/"/merge"/}";
      BRANCH="${BRANCH//"/"/"-"}"
    fi
    end_time=$(date +%s%N)
    echo "=== [ðŸŒ³] set env variables: $(((end_time - start_time) / 1000000))ms ==="
fi

# [â¬†] update emojis
if [[ $_RUN_STEP_UPDATE_EMOJIS = true ]]; then
    start_time=$(date +%s%N)
    echo "### [â¬†] update emojis ###"
    yarn ts-node scripts/utils/fetchEmojis.ts
    end_time=$(date +%s%N)
    echo "=== [â¬†] update emojis: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸŽ¨] format files not covered by ESLint
if [[ $_RUN_STEP_FORMAT = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸŽ¨] format files not covered by ESLint ###"
    yarn ts-node scripts/format.ts || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸŽ¨] format files not covered by ESLint: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸš¨] run ESLint
if [[ $_RUN_STEP_ESLINT = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸš¨] run ESLint ###"
    yarn eslint \
    ./docs/.vuepress/ \
    ./static/         \
    ./prebuild/       \
    ./build/          \
    ./src/            \
    ./scripts/        \
    ./typings/        \
    --ext .js,.ts,.vue,.md \
    --no-error-on-unmatched-pattern \
    --exit-on-fatal-error \
    --report-unused-disable-directives \
    --cache --cache-strategy content \
    --fix \
    || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸš¨] run ESLint: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸš¨] check TypeScript
if [[ $_RUN_STEP_TSC = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸš¨] check TypeScript ###"
    yarn tsc -b --pretty "./" || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸš¨] check TypeScript: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸ“œ] build userscript
if [[ $_RUN_STEP_USERSCRIPT = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸ“œ] build userscript ###"
    yarn tsc --pretty --project "src/tsconfig.userscript.json" || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸ“œ] build userscript: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸ“œ] build buildscript
if [[ $_RUN_STEP_BUILDSCRIPT = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸ“œ] build buildscript ###"
    yarn ts-node scripts/createBuildScript.ts || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸ“œ] build buildscript: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸš§] run prebuild
if [[ $_RUN_STEP_PREBUILD = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸš§] run prebuild ###"
    yarn ts-node prebuild/index.ts "$MODE" "$BRANCH" "ðŸ¦„ branch label" || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸš§] run prebuild: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸ‘·] webpack
if [[ $_RUN_STEP_WEBPACK = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸ‘·] webpack ###"
    yarn ts-node build/index.ts --esModuleInterop "$MODE" "$BRANCH" "ðŸ¦„ branch label" || exit 1
    end_time=$(date +%s%N)
    echo "=== [ðŸ‘·] webpack: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸ“] build docs
if [[ $_RUN_STEP_DOCS = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸ“] build docs ###"
    "$(yarn workspace lss-manager-v4-docs bin vuepress)" build docs || exit 1
    mkdir -p ./dist/docs
    rm -rdf ./dist/docs/*
    cp -r ./docs/.vuepress/dist/* ./dist/docs
    end_time=$(date +%s%N)
    echo "=== [ðŸ“] build docs: $(((end_time - start_time) / 1000000))ms ==="
fi

# [â„¹ï¸] git diff
if [[ $_RUN_STEP_GIT_DIFF = true ]] && [[ $GIT_REPO = true ]]; then
    start_time=$(date +%s%N)
    echo "### [â„¹ï¸] git diff ###"
    git --no-pager diff --color-words
    end_time=$(date +%s%N)
    echo "=== [â„¹ï¸] git diff: $(((end_time - start_time) / 1000000))ms ==="
fi

# [ðŸ“¦] zip files
if [[ $_RUN_STEP_ZIP = true ]]; then
    start_time=$(date +%s%N)
    echo "### [ðŸ“¦] zip files ###"
    sudo apt-get install zip
    zip -r dist.zip dist
    end_time=$(date +%s%N)
    echo "=== [ðŸ“¦] zip files: $(((end_time - start_time) / 1000000))ms ==="
fi

total_end_time=$(date +%s%N)

echo "=== Total: $(((total_end_time - total_start_time) / 1000000))ms ==="